            <div class="final">
                <div class="titulo-final">Grande Final</div>
                <table class="tabela-final">
                    <td>{Final()}</td>
                </table>
            </div>
            <div class="campeao">
                <p>
                    O grande campeão da Copa do Mundo será:
                </p>
                <img class="bandeira-campeao" src={`../imgs/${campeao[0]}.png`} />
                <p>{campeao[0]}</p>
            </div>
        </Fragment>
    )
}
