import axios from "axios"
import { useEffect, useState } from "react"

export function Teamlist() {
    const [teams, setTeams] = useState([]);

    useEffect(() => {
        axios.get('https://estagio.geopostenergy.com/WorldCup/GetAllTeams',{
            headers: {
                'git-user': 'theoalmeida'
            }
        })
            .then(({data}) => {
                setTeams(data);
            });
            // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])  
    var lista = []
    for (const pais in teams["Result"]) {
        lista.push(teams["Result"][pais]["Name"])
    };

    var grupoA = [];
    var grupoB = [];
    var grupoC = [];
    var grupoD = [];
    var grupoE = [];
    var grupoF = [];
    var grupoG = [];
    var grupoH = [];

    var x = 0 // variavel de apoio para o sorteio
    while (x < 32) {
        var y = Math.floor(Math.random()*((31-x)- 0 + 1)) + 0  
        if (x <= 3) {
            grupoA.push(lista[y])
        } else if (x > 3 && x <= 7) {
            grupoB.push(lista[y])
        } else if (x > 7 && x <= 11) {
            grupoC.push(lista[y])
        } else if (x > 11 && x <= 15) {
            grupoD.push(lista[y])
        } else if (x > 15 && x <= 19) {
            grupoE.push(lista[y])
        } else if (x > 19 && x <= 23) {
            grupoF.push(lista[y])
        } else if (x > 20 && x <= 27) {
            grupoG.push(lista[y])
        } else if (x > 27 && x <= 31) {
            grupoA.push(lista[y])
        }
        lista.splice(lista[y], 1)
        x = x + 1
    }

    return (
        <div>
        <div>Grupo A: {grupoA[0]}, {grupoA[1]}, {grupoA[2]}, {grupoA[3]}</div>
        <div>Grupo B: {grupoB[0]}, {grupoA[1]}, {grupoA[2]}, {grupoA[3]}</div>
        <div>Grupo C: {grupoC[0]}, {grupoA[1]}, {grupoA[2]}, {grupoA[3]}</div>
        <div>Grupo D: {grupoD[0]}, {grupoA[1]}, {grupoA[2]}, {grupoA[3]}</div>
        <div>Grupo E: {grupoAE[0]}, {grupoA[1]}, {grupoA[2]}, {grupoA[3]}</div>
        <div>Grupo F: {grupoA[0]}, {grupoA[1]}, {grupoA[2]}, {grupoA[3]}</div>
        <div>Grupo G: {grupoA[0]}, {grupoA[1]}, {grupoA[2]}, {grupoA[3]}</div>
        <div>Grupo H: {grupoA[0]}, {grupoA[1]}, {grupoA[2]}, {grupoA[3]}</div>
        </div>
    )
}
