import axios from "axios"
import { useEffect, useState } from "react"

export function Teamlist() {
    const [teams, setTeams] = useState([]);

    useEffect(() => {
        axios.get('https://estagio.geopostenergy.com/WorldCup/GetAllTeams',{
            headers: {
                'git-user': 'theoalmeida'
            }
        })
            .then(({data}) => {
                setTeams(data);
            });
            // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])  
    var lista = []
    for (const pais in teams["Result"]) {
        lista.push(teams["Result"][pais]["Name"])
    };

    var grupoA = [];
    var grupoB = [];
    var grupoC = [];
    var grupoD = [];
    var grupoE = [];
    var grupoF = [];
    var grupoG = [];
    var grupoH = [];

    var x = 0 // variavel de apoio para o sorteio
    while (x < 32) {
        var y = Math.floor(Math.random()*((31-x)- 0 + 1)) + 0  
        if (x <= 3) {
            grupoA.push(lista[y])
            console.log(grupoA, x)
        } else if (x > 3 && x <= 7) {
            grupoB.push(lista[y])
        } else if (x > 7 && x <= 11) {
            grupoC.push(lista[y])
        } else if (x > 11 && x <= 15) {
            grupoD.push(lista[y])
        } else if (x > 15 && x <= 19) {
            grupoE.push(lista[y])
        } else if (x > 19 && x <= 23) {
            grupoF.push(lista[y])
        } else if (x > 20 && x <= 27) {
            grupoG.push(lista[y])
        } else if (x > 27 && x <= 31) {
            grupoH.push(lista[y])
        }
        lista.splice(y, 1)
        x += 1
    }

    return (
        <div class="faseDeGrupos">
        <div class="titulo">Grupos sorteados: </div>
        <div class="grupo">Grupo A: <img src={`/imgs/flags/.png`} />{grupoA[0]}, {grupoA[1]}, {grupoA[2]}, {grupoA[3]}</div>
        <div class="grupo">Grupo B: {grupoB[0]}, {grupoB[1]}, {grupoB[2]}, {grupoB[3]}</div>
        <div class="grupo">Grupo C: {grupoC[0]}, {grupoC[1]}, {grupoC[2]}, {grupoC[3]}</div>
        <div class="grupo">Grupo D: {grupoD[0]}, {grupoD[1]}, {grupoD[2]}, {grupoD[3]}</div>
        <div class="grupo">Grupo E: {grupoE[0]}, {grupoE[1]}, {grupoE[2]}, {grupoE[3]}</div>
        <div class="grupo">Grupo F: {grupoF[0]}, {grupoF[1]}, {grupoF[2]}, {grupoF[3]}</div>
        <div class="grupo">Grupo G: {grupoG[0]}, {grupoG[1]}, {grupoG[2]}, {grupoG[3]}</div>
        <div class="grupo">Grupo H: {grupoH[0]}, {grupoH[1]}, {grupoH[2]}, {grupoH[3]}</div>
        </div>
    )
}
