import { lista_paises } from "../selecoes/selecoes";

export function SorteioGrupos() {
    var grupoA = [];
    var grupoB = [];
    var grupoC = [];
    var grupoD = [];
    var grupoE = [];
    var grupoF = [];
    var grupoG = [];
    var grupoH = [];

    var x = 0 // variavel de apoio para o sorteio
    while (x < 32) {
        var y = Math.floor(Math.random()*(31- 0 + 1)) + 0  
        if (x <= 3) {
            grupoA.push(lista_paises[y])
            lista_paises.splice(y)
        } else if (x > 3 && x <= 7) {
            grupoB.push(lista_paises[y])
            lista_paises.splice(y)
        } else if (7 < x && x <= 11) {
            grupoC.push(lista_paises[y])
            lista_paises.splice(y)
        } else if (11 < x && x <= 15) {
            grupoD.push(lista_paises[y])
            lista_paises.splice(y)
        } else if (15 < x && x <= 19) {
            grupoE.push(lista_paises[y])
            lista_paises.splice(y)
        } else if (19 < x && x <= 23) {
            grupoF.push(lista_paises[y])
            lista_paises.splice(y)
        } else if (23 < x <= 27) {
            grupoG.push(lista_paises[y])
            lista_paises.splice(y)
        } else if (27 < x <= 31) {
            grupoA.push(lista_paises[y])
            lista_paises.splice(y)
        }
    }
    return (
        <div>
        <div>{grupoA}</div>
        <div>{grupoB}</div>
        <div>{grupoC}</div>
        <div>{grupoD}</div>
        <div>{grupoE}</div>
        <div>{grupoF}</div>
        <div>{grupoG}</div>
        <div>{grupoH}</div>
        </div>
    )
}
